const { default: Posts } = require("../components/Posts/Posts");

const reducer = (state=[], action) => {
    switch (action.type) {
        case 'FETCH_ALL':
            return state;
            break;
        case 'CREATE':
            return state;
            break;
        default:
            return posts;
    }
}
